<?phpnamespace Library; abstract class FormBuilder{  protected $form;  protected $data;     public function __construct(Entity $entity)  {    $this->setForm(new Form($entity));  }     abstract public function build();     public function setForm(Form $form)  {    $this->form = $form;  }  public function form()  {    return $this->form;  }  public function setData(Array $data)  {    $this->data = $data;  }  public function data()  {    return $this->data;  }}?>